import React from "react";
import WeatherIcon from "./WeatherIcon";

function DailyForecastWidget({ data }) {
  const {
    day,
    icon,
    summary,
    temperature_max,
    temperature_min,
    precipitation,
  } = data;

  // date format
  const now_date = {
    day: new Intl.DateTimeFormat(navigator.language, {
      weekday: "short",
      day: "2-digit",
      month: "2-digit",
    }).format(new Date()),
    time: new Intl.DateTimeFormat(navigator.language, {
      hour: "2-digit",
      minute: "2-digit",
    }).format(new Date().setMinutes(0)),
  };

  const weather_date = {
    day: new Intl.DateTimeFormat(navigator.language, {
      weekday: "short",
      day: "2-digit",
      month: "2-digit",
    }).format(new Date(day)),
    time: new Intl.DateTimeFormat(navigator.language, {
      hour: "2-digit",
      minute: "2-digit",
    }).format(new Date(day).setMinutes(0)),
  };

  weather_date.day =
    weather_date.day === now_date.day && weather_date.time === now_date.time
      ? "Today"
      : weather_date.time === "12:00 AM"
      ? weather_date.day
      : "";

  return (
    <div className="widget">
      <div className="day">{}</div>
      <div className="icon-temp">
        <div className="icon">
          <WeatherIcon iconNumber={icon} summary={summary} />
        </div>
        <div className="temperature">
          <div className="max">{Math.round(temperature_max)} °C</div>
          <div className="min">{Math.round(temperature_min)} °C</div>
        </div>
      </div>
      <div className="precipitation">
        {Math.round(precipitation.total)} mm/h
      </div>
    </div>
  );
}

export default DailyForecastWidget;
